#include "angle.h"

string Angle::read_svg(Angles a) {
    string* ang = new string[4];
    // 65,44 Antonio https://fontsvg.com
    ang[0] = "";
    // 37,44
    ang[1] = "";
    // 87,44
    ang[2] = "";
    // 53, 44
    ang[3] = "";
    string r = "data:image/svg+xml;base64,";
    switch (a) {
        case Angles::asc:
            return r + ang[0];
        case Angles::fc:
            return r + ang[1];
        case Angles::desc:
            return r + ang[2];
        case Angles::mc:
            return r + ang[3];
        case Angles::nothing:
            return r + ang[0];
    }
}