#include "angle.h"

string Angle::read_svg(Angles a) {
    string* ang = new string[4];
    //ang[0] = "";
    //ang[1] = "";
    //ang[2] = "";
    //ang[3] = "";
    //string r = "data:image/svg+xml;base64,";
    ang[0] = "";
    ang[1] = "";
    ang[2] = "";
    ang[3] = "";
    string r = "data:image/png;base64,";
    switch (a) {
        case Angles::asc:
            return r + ang[0];
        case Angles::fc:
            return r + ang[1];
        case Angles::desc:
            return r + ang[2];
        case Angles::mc:
            return r + ang[3];
        case Angles::nothing:
            return r + ang[0];
    }
}