#include "astre.h"

string Astre::read_svg(Astres astre) {
    string* a = new string[14];
    a[0] = "";
    a[1] = "";
    a[2] = "";
    a[3] = "";
    a[4] = "";
    a[5] = "";
    a[6] = "";
    a[7] = "";
    a[8] = "";
    a[9] = "";
    a[10] = "";
    a[11] = "";
    a[12] = "";
    a[13] = a[10]; // TODO noeud lunaire sud depuis projet Xcode
    string r = "data:image/svg+xml;base64,";
    switch (astre) {
        case soleil:
            return r + a[0];
        case lune:
            return r + a[1];
        case mercure:
            return r + a[2];
        case venus:
            return r + a[3];
        case mars:
            return r + a[4];
        case jupiter:
            return r + a[5];
        case saturn:
            return r + a[6];
        case uranus:
            return r + a[7];
        case neptune:
            return r + a[8];
        case pluto:
            return r + a[9];
        case noeud_lunaire:
            return r + a[10];
        case chiron:
            return r + a[11];
        case ceres:
            return r + a[12];
        case noeud_lunaire_sud:
            return r + a[13];
    }
}

Image Astre::generer(Astres astre, double x, double y, double width, double height) {
    return Image(Point(x, y), width , height , Astre::read_svg(astre), Color::Black);
}

string Astre::nom(Astres a) {
    switch (a) {
        case Astres::soleil:
            return "Soleil";
        case Astres::lune:
            return "Lune";
        case Astres::mercure:
            return "Mercure";
        case Astres::venus:
            return "Venus";
        case Astres::mars:
            return "Mars";
        case Astres::jupiter:
            return "Jupiter";
        case Astres::saturn:
            return "Saturn";
        case Astres::uranus:
            return "Uranus";
        case Astres::neptune:
            return "Neptune";
        case Astres::pluto:
            return "Pluton";
        case Astres::noeud_lunaire:
            return "Noeud lunaire";
        default:
            return "";
    }
}

string* Astre::sign_name_and_svg(CalcUt calcul_ut) {
    string* res = new string[2];
    Signs s = Signs::belier;
    for (int i = 1; i < 13; ++i) {
        s = static_cast<Signs>(i);
        double pos = (i - 1) * 30;
        if (pos > 360) {
            pos -= 360;
        }
        if (calcul_ut.longitude >= pos && calcul_ut.longitude <= pos + 30) {
            res[0] = Sign::nom(s);
            res[1] = Sign::read_svg(s);
            return res;
        }
    }
    res[0] = Sign::nom(s);
    res[1] = Sign::read_svg(s);
    return res;
}

