#include "sign.h"

string Sign::read_svg(Signs sign) {
    string* s = new string[12];
    s[0] = "";
    s[1] = "";
    s[2] = "";
    s[3] = "";
    s[4] = "";
    s[5] = "";
    s[6] = "";
    s[7] = "";
    s[8] = "";
    s[9] = "";
    s[10] = "";
    s[11] = "";
    string r = "data:image/svg+xml;base64,";
    switch (sign) {
        case Signs::belier:
            return r + s[0];
        case Signs::taureau:
            return r + s[1];
        case Signs::gemaux:
            return r + s[2];
        case Signs::cancer:
            return r + s[3];
        case Signs::lion:
            return r + s[4];
        case Signs::vierge:
            return r + s[5];
        case Signs::balance:
            return r + s[6];
        case Signs::scorpion:
            return r + s[7];
        case Signs::sagittaire:
            return r + s[8];
        case Signs::capricorne:
            return r + s[9];
        case Signs::verseau:
            return r + s[10];
        case Signs::poisson:
            return r + s[11];
    }
}

string Sign::nom(Signs sign) {
    switch (sign) {
        case belier:
            return "Belier";
        case taureau:
            return "Taureau";
        case gemaux:
            return "Gemaux";
        case cancer:
            return "Cancer";
        case lion:
            return "Lion";
        case vierge:
            return "Vierge";
        case balance:
            return "Balance";
        case scorpion:
            return "Scorpion";
        case sagittaire:
            return "Sagittaire";
        case capricorne:
            return "Capricorne";
        case verseau:
            return "Verseau";
        case poisson:
            return "Poisson";
    }
}

